    //Banner Authentication with LDAP - middlware auth

    var url = $rootScope.middlewareServerUrl + "/bannermobile2/services/authenticate/login";
    var data = {};
    if (window.device) {
        data = "username=" + uname + "&password=" + password;
    } else {
        url = "/websimulator/json?url=" + url;
        data = {
            method: "POST",
            body: "username=" + uname + "&password=" + password
        };
    }
    var authUrl = url;
    var authData = data;
    $rootScope.generateST = function(tgt, service, callback) {
        //Bypassing the ST as there is no service ticket required here..
        callback(tgt);
    };

    $rootScope.postAPI = function(endpoint, postdata, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "POST";
            var proxyData = postdata;
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + serviceurl;
                proxyMethod = "POST";
                proxyData = {
                    method: "POST",
                    body: postdata
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };

    $rootScope.getAPI = function(endpoint, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };




    $.blockUI({
        message: '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3>Authenticating</h3></div>'
    });

    $http.post(url, data).success(function(data, status, headers, config) {
        success(data);
        $.unblockUI();
    }).error(function(data, status, headers, config) {
        error(data);
        $.unblockUI();
    });

}

pages.pageprocessor:
function pageprocessorBlueLightEmergency50(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $rootScope.fromEmail = pagedef.fromEmail ? pagedef.fromEmail : 'donotreply@blueLight.com';
  $rootScope.collegeName = pagedef.collegename ? pagedef.collegename : '';
  $rootScope.universityPhones = pagedef.universityphones ? pagedef.universityphones : [];
  $rootScope.apikey = pagedef.apikey;
  $rootScope.androidDownloadLink = pagedef.android;
  $rootScope.iosDownloadLink =  pagedef.ios;


  if ($.jStorage.get('termsCondition') == null || $.jStorage.get('termsCondition') == undefined) {
    $.unblockUI();
    $location.path("/app/BlueLightEmergency50/BlueLightEmergency50Page5");
    return;
  }
  $rootScope.showlogin = true;
  setTimeout(function() {
    $.unblockUI();
  }, 2000);
  if (!$rootScope.user) {

    $rootScope.user = {};

  }

  if ($location.search().email != undefined && $location.search().email != '') {

    $rootScope.emailTrack = $location.search().email;


  }

  if (!pagedef.IDPCLIENT && ($.jStorage.get('email') == undefined || $.jStorage.get('email') == '')) {
    $.unblockUI();
    navigator.notification.prompt(" Please enter your Email ID.\n Note:Email ID can't be changed.", onPrompt, 'Registration', ['Register'], '');

    function onPrompt(results) {

      if (results.input1 == "") {

        navigator.notification.prompt(" Please enter your Email ID.\nNote: Email ID can't be changed.)", onPrompt, 'Registration', ['Register'], '');

      } else {
        var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if (!re.test(String(results.input1).toLowerCase())) {


          navigator.notification.prompt(" Please enter your Email ID.\nNote: Email ID can't be changed.)", onPrompt, 'Registration', ['Register'], '');
        } else {


          $rootScope.user.email = results.input1.toLowerCase();
          $.jStorage.set('email', results.input1.toLowerCase());
        }
      }

    }
  }
  if ($.jStorage.get('email') != undefined && $.jStorage.get('email') != '') {


    $rootScope.user.email = $.jStorage.get('email').toLowerCase();

  }

  var globalUrl = pagedef.url
  if (!globalUrl || globalUrl == null) {
    alert("Please enter url from studio");
    retrun;
  }

  $.jStorage.set('url', globalUrl);

  if (window.device) {

    $.jStorage.set('tenantId', MyCampusApp.config.tenant);



  } else {

    $.jStorage.set('tenantId', 'unifyeddev');

  }


  var tenantId = $.jStorage.get('tenantId');



  $scope.flag = false;
  var url = '';
  var userInfo;
  $scope.gurUpUser = {};
  $scope.guardiansList = [];

  var usertrackingInterval;
  var gudata;
  if ($rootScope.guardianTrackingShow == undefined)
    $rootScope.guardianTrackingShow = true;
  // var script = document.createElement("script");

  // Get the first script element on the page
  var ref = window.document.getElementsByTagName('script')[0];

  // Create a new script element
  var script = window.document.createElement('script');

  // Set the script element `src`
  //script.src = 'https://maps.googleapis.com/maps/api/js?key=AIzaSyAWtMIxW71c0fxIUmgWGe4AfUb2D9sE-5o';
  script.src = "https://maps.googleapis.com/maps/api/js?key=" + $rootScope.apikey;
  // Inject the script into the DOM
  ref.parentNode.insertBefore(script, ref);

  // script.type = "text/javascript";

  //script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyBf-3Ff01fEYn-d6qC6mXaVAbKvOkPLOTY";


  // script.src = "https://maps.googleapis.com/maps/api/js?key=" + $rootScope.apikey
  //script.id = "googleMapApi";
  if ($('#googleMapApi').html() == '')
    document.body.appendChild(script);
  var mapDisplay = document.getElementById("MapMapCanvas");
  var mapheight = $(window).height() - ($("#appContent .list-group-item").height() + $(".navbar-absolute-top").height() + $(".navbar-absolute-bottom").height() + 100)
  $("#MapMapCanvas").css("height", mapheight + "px");




  $scope.getUserTrackingDetails = function(trackMe) {

    var lat = trackMe.lat;
    var lng = trackMe.longitude;
    var latlng = new google.maps.LatLng(lat, lng);
    var myOptions = {
      zoom: 14,
      center: latlng,
      mapTypeId: google.maps.MapTypeId.ROADMAP
    };
    map = new google.maps.Map(mapDisplay, myOptions);
    //$scope.createMarker($scope.gurUpUser.fname + " "+ $scope.gurUpUser.lname +' current location', latlng)
    map.addMarker($scope.createMarker($scope.gurUpUser.fname + " " + $scope.gurUpUser.lname + ' current location ', latlng));
    //$scope.addMarker(latlng);
  }

  $scope.createMarker = function(text, latlng) {


    var marker = new google.maps.Marker({
      position: latlng,
      map: map
    });
    infowindow = new google.maps.InfoWindow({
      content: text
    });
    infowindow.open(map, marker);
    return marker;
  }

  $scope.trackUserInterval = function() {


    if (($rootScope.user == undefined || $rootScope.user == null || $rootScope.user == '') && ($rootScope.emailTrack != undefined && $rootScope.emailTrack == '')) {

      var refreshUrl = $.jStorage.get('url') + "/personal/" + $scope.gurUpUser.email;

      $http({
        method: 'GET',
        url: refreshUrl,
        headers: {
          "X-Tenant-ID": tenantId,
          "Content-Type": "application/json"
        },
        data: ''

      }).then(function(res) {
        $scope.gurUpUser = res.data;

        if (!$scope.gurUpUser.trackMe.active) {

          clearInterval(usertrackingInterval);

          if (window.device) {
            navigator.notification.alert("The user stopped tracking.");

          } else {
            alert("The user stopped tracking.");
          }
          $rootScope.guardianTrackingShow = true;
          $rootScope.usertracking = false;
          $rootScope.emailTrack = '';

        } else {

          var trackMe = res.data.trackMe;

          if ($scope.flag == false) {

            $scope.flag = true;
            if (window.device) {

              navigator.notification.alert("You are tracking Live location of: " + $scope.gurUpUser.fname + " " + $scope.gurUpUser.lname);

            } else {

              alert("You are tracking Live location of: " + $scope.gurUpUser.fname + " " + $scope.gurUpUser.lname);
            }
          }
          $scope.getUserTrackingDetails(trackMe);
        }



      }, function(error) {

        if (window.device) {
          navigator.notification.alert("Error");
        } else {
          alert("Error");
        }

      });

    } else {

      var proxyMethod = "POST";


      $scope.guardiansList = [];

      var refreshUrl = $.jStorage.get('url') + "/personal/" + $scope.gurUpUser.email;

      $http({
        method: 'GET',
        url: refreshUrl,
        headers: {
          "X-Tenant-ID": tenantId,
          "Content-Type": "application/json"
        },
        data: ''

      }).then(function(res) {

        setTimeout(function() {

          $scope.gurUpUser = res.data;
          $scope.guardiansList = $scope.gurUpUser.guardiansList;

          for (var i = 0; i < $scope.guardiansList.length; i++) {

            if ($scope.guardiansList[i].gudemail == $rootScope.user.email) {

              $scope.guardiansList[i].guardianTracking = true;
              break;
            }
          }


          url = $.jStorage.get('url') + "/personal/" + $scope.gurUpUser.email;
          $scope.gurUpUser.guardiansList = $scope.guardiansList;

          var proxyData = $scope.gurUpUser;


          $http({
            method: proxyMethod,
            url: url,
            headers: {
              "X-Tenant-ID": tenantId,
              "Content-Type": "application/json"
            },
            data: proxyData

          }).then(function(response) {



            if (!response.data.trackMe.active) {

              clearInterval(usertrackingInterval);

              if (window.device) {
                navigator.notification.alert("The user stopped tracking.");

              } else {
                alert("The user stopped tracking.");
              }
              $rootScope.guardianTrackingShow = true;
              $rootScope.usertracking = false;
              $rootScope.emailTrack = '';

            } else {

              var trackMe = response.data.trackMe;

              if ($scope.flag == false) {

                $scope.flag = true;
                if (window.device) {

                  navigator.notification.alert("You are tracking Live location of: " + $scope.gurUpUser.fname + " " + $scope.gurUpUser.lname);

                } else {

                  alert("You are tracking Live location of: " + $scope.gurUpUser.fname + " " + $scope.gurUpUser.lname);
                }
              }
              $scope.getUserTrackingDetails(trackMe);
            }


          });


        }, 1000);

      }, function(error) {

        if (window.device) {
          navigator.notification.alert("Error");
        } else {
          alert("Error");
        }

      });

    }


  }
  $scope.trackMeConfirmation = function(btn) {

    if (btn == 1) {
      clearInterval(usertrackingInterval);
      $rootScope.usertracking = false;
      $rootScope.guardianTrackingShow = true;
      $rootScope.emailTrack = '';

      if (window.device) {

        navigator.notification.alert("Tracking stopped");
        $location.path("#/app/BlueLightEmergency50/BlueLightEmergency50");
      } else {
        alert("Tracking stopped");
        $location.path("/app/BlueLightEmergency50/BlueLightEmergency50");
      }


    }
  }

  $scope.UpdateGuardianStatus = function(gEmail) {
    var proxyMethod = "GET";

    url = $.jStorage.get('url') + "/personal/" + gEmail;

    var proxyData = gEmail;

    $http({
      method: proxyMethod,
      url: url,
      headers: {
        "X-Tenant-ID": tenantId,
        "Content-Type": "application/json"
      },
      data: proxyData

    }).then(function(response) {

      var refreshData = response.data;
      if (!refreshData.trackMe.active) {


        if (window.device) {
          navigator.notification.alert("The user stopped tracking.");
        } else {
          alert("The user stopped tracking.");
        }
        $rootScope.usertracking = false;

      } else {

        $scope.gurUpUser = response.data;

        var time = $scope.gurUpUser.additionalInfo.timeInterval;
        if (parseInt(time) < 5)
          time = time * 5;
        time = time * 1000;
        console.log("time", time);
        usertrackingInterval = setInterval($scope.trackUserInterval, time);
        $scope.trackUserInterval();

      }

    }, function(data, status, headers, config) {

    });
  }

  if ($rootScope.emailTrack != '' && $rootScope.emailTrack != undefined) {


    $rootScope.usertracking = true;
    $rootScope.guardianTrackingShow = false;
    $scope.UpdateGuardianStatus($rootScope.emailTrack);

  }

  $scope.StopTrackingUser = function() {
    if (!$rootScope.usertracking == true) {
      $rootScope.guardianTrackingShow = true;
      $rootScope.emailTrack = '';
      clearInterval(usertrackingInterval);
      $location.path("#/app/BlueLightEmergency50/BlueLightEmergency50");
    } else {

      if (window.device) {
        navigator.notification.confirm("You are tracking user location. Are you sure you want to stop.", $scope.trackMeConfirmation, 'Confirmation', ['Yes', 'No']);
      } else {
        $scope.trackMeConfirmation(1);
      }

    }
  }


  $rootScope.back = function() {
    if ($rootScope.usertracking == false) {
      clearInterval(usertrackingInterval);
      $location.path("/app/BlueLightEmergency50/BlueLightEmergency50");
    } else {
      $scope.StopTrackingUser();

    }
  }

  if (pagedef.icon_url) {

    $rootScope.icon_url = pagedef.icon_url;
  }


  $scope.OpenClicked = function(id) {
    switch (id) {
      case 'profile':
        {
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page6');
          break;
        }
      case 'guardians':
        {
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page2');
          break;
        }

      case 'preference':
        {
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page7');
          break;
        }
      case 'share':
        {
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page17');

          break;
        }
      case 'places':
        {
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page12');
          break;
        }
      case 'sendalert':
        {
          //$scope.AlertSend();
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page10');
          break;
        }
      case 'trackme':
        {
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page8');
          break;
        }
      case 'trackuser':
        {
          $location.path('/app/BlueLightEmergency50/BlueLightEmergency50Page9');
          break;
        }
    }
  }



  $scope.getPersonal = function() {




    url = $.jStorage.get('url') + "/personal";
    url = url + "/" + $rootScope.user.email;


    var proxyMethod = "GET";

    var data = {};
    var proxyData = data;

    $http({
      method: proxyMethod,
      url: url,
      headers: {
        "X-Tenant-ID": tenantId,
        "Content-Type": "application/json"
      },
      data: proxyData

    }).then(function(response) {


      if (response.data != '') {

        $scope.status = response.status;

        if (response.data != '') {
          $scope.data = response.data;


          $.jStorage.set("PersonalInfo", $scope.data);

          $rootScope.PersonalInfo = response.data;

        }
      }

    });

  }

  if ($rootScope.user.email)

    $scope.getPersonal();




}

  function pageprocessorBlueLightEmergency50Page2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {


    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);

    $rootScope.back = function() {
      $location.path("/app/BlueLightEmergency50/BlueLightEmergency50");
