    $scope.SendPush();
    $rootScope.TrackMeStarted = !0;
    time = PI.additionalInfo.timeInterval;
    if (parseInt(time) < 5)
      time = time * 5;
    time = time * 1000;
    $scope.trackingFinished = false;
    trackMeInterval = setInterval(function() {
      $scope.sendTrackingLocation();
    }, time)
  }
  $scope.sendTrackingLocation = function() {
    console.log("time " + time);
    navigator.geolocation.getCurrentPosition(function(loc) {
      var lat = loc.coords.latitude;
      var long = loc.coords.longitude;
      var trackMe = {};
      trackingID = Math.random();
      trackMe._id = $rootScope.PersonalInfo._id, trackMe.trackingId = trackingID, trackMe.lat = lat, trackMe.longitude = long;
      if ($scope.trackingFinished) {
        trackMe.active = false;
      } else {
        trackMe.active = true;
      }
      var personalInfodata = {};
      $scope.PerEmail = $rootScope.user.email;
