        personalInfodata = $rootScope.PersonalInfo;
        personalInfodata.trackMe = trackMe;
        var proxyData = personalInfodata;
        console.info('sendTrackingLocation', proxyData);
        var proxyMethod = "POST";
        $http({
          method: proxyMethod,
          url: url,
          headers: {
            "X-Tenant-ID": tenantId,
            "Content-Type": "application/json"
          },
          data: proxyData
        }).then(function(response) {
          $scope.GuardiansActiveList = [];
          $rootScope.PersonalInfo = response.data;
          var guardiansList = [];
