          }
          trackingID = $rootScope.PersonalInfo.trackMe.trackingId;
          if (trackingID != 'undefined' || trackingID != '' || trackingID != null) {
            if ($scope.flag == !1) {
              $scope.flag = !0;
              if (window.device) {} else {}
            }
          }
        })
      }, function(error) {
        if ($scope.errFlag == !1) {
          $scope.errFlag = !0;
