            clearInterval(trackMeInterval);
            $scope.finishTracking();
            navigator.notification.alert("GPS Error, Check Location access permission for the App.")
          } else {
            clearInterval(trackMeInterval);
            $scope.finishTracking();
            alert("GPS Error, Check Location access permission for the App.")
          }
        }
      })
    }, onError, options)
  }

  $scope.finishTracking = function() {
    $scope.trackingFinished = true;
    clearInterval(trackMeInterval);
    setTimeout($scope.changeUserTrackingStatus, time);
    $rootScope.TrackMeStarted = !1;
    var proxyMethod = "POST";
    var trackMe = {};
    trackMe = $rootScope.PersonalInfo.trackMe;
    trackMe.active = !1;
