    var map = new google.maps.Map(mapDisplay, myOptions);
    var marker = new google.maps.Marker({
      position: latlng,
      map: map,
      title: $rootScope.place.result.name
    });
  } catch (e) {
    alert("Error : " + e);
  }
}

function pageprocessorBlueLightEmergency50Page17(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {


  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);


  $rootScope.back = function() {
    $location.path("/app/BlueLightEmergency50/BlueLightEmergency50");
  };



  var onError = function(err) {
    $.unblockUI();
    switch (err.code) {
      case 3:
        {
          var errmsg = "Could not get the current position. Either GPS signals are weak or GPS has been switched off.";
          break;
        }
      case 1:
        {
          var errmsg = "Either GPS signals are weak or GPS has been switched off.";
          break;
        }
      case 2:
        {
          var errmsg = "Unable to get your current position.";
          break;
        }
    }
    if (window.device) {
      navigator.notification.alert('To continue, turn on Device Location');
      $location.path("/app/BlueLightEmergency50/BlueLightEmergency50");
    } else {
      alert(errmsg);
    }
  }

  var ShareLocation = function(data) {
    $.unblockUI();

    var PI = $.jStorage.get('PersonalInfo');
    var message = PI.fname + " " + PI.lname + " location: http://maps.google.com/?q=" + data.coords.latitude + "," + data.coords.longitude;
    var subject = "Sharing location of " + PI.fname + " " + PI.lname
    try {
      window.plugins.socialsharing.share(message, subject);
    } catch (e) {
      alert(e)
    }
    $location.path("/app/BlueLightEmergency50/BlueLightEmergency50");

  }

  $.blockUI();
  var options = {
    timeout: 5000,
    frequency: 3000,
    enableHighAccuracy: true
  };

  /*var defaultGD = [
  {'GuardianPhn':'3028571110','guardianName':'Dover Public Safety – Main Phone','email':'owens-bluelight@dtcc.edu','enable':true},
  {'GuardianPhn':'3024543938','guardianName':'Georgetown Public Safety – Main Phone','email':'george-bluelight@dtcc.edu','enable':true},
  {'GuardianPhn':'3025735418','guardianName':'Wilmington Public Safety – Main Phone','email':'terry-bluelight@dtcc.edu','enable':true},
  {'GuardianPhn':'3022420362','guardianName':'Stan Public Safety – Main Phone','email':'stan-bluelight@dtcc.edu','enable':true}];
  */
  if ($.jStorage.get("PersonalInfo") == null) {
    if (window.device) {
      $.unblockUI();
      navigator.notification.alert("Please update your profile before sharing location.");
      $location.path("/app/BlueLightEmergency50/BlueLightEmergency50Page6");
    } else {
      alert("Please update your profile before sharing location.");
    }
  } else {
    navigator.geolocation.getCurrentPosition(ShareLocation, onError, options);
  }

  /*var message="I need non-urgent assistance\n\n";
  for(var i=0;i<defaultGD.length;i++){
    message +=defaultGD[i].guardianName+": "+defaultGD[i].GuardianPhn+"\n";
  }
        
  window.plugins.socialsharing.share(message);*/

}

function pageprocessorBlueLightEmergency50Page18(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    try {

        $scope.walk = false;
        $scope.cycle = false;
        $scope.transit = false;
        $scope.drive = false;
       

        $scope.changeMode = function(mode) {

            var latlng = new google.maps.LatLng($rootScope.place.result.geometry.location.lat, $rootScope.place.result.geometry.location.lng);
            var myOptions = {
                zoom: 16,
                mapTypeId: google.maps.MapTypeId.ROADMAP
            };
            var mapDisplay = document.getElementById("Place-map-canvas")
            var map = new google.maps.Map(mapDisplay, myOptions);
            var directionsDisplay = new google.maps.DirectionsRenderer({
                suppressMarkers: true
            });
            var icons = {
                start: new google.maps.MarkerImage(

                    // URL
                    'images/start-pin.png',
                    // (image width,height)
                    new google.maps.Size(32, 32),
                    //Point for the Image
                    new google.maps.Point(0, 0)
                ),
                end: new google.maps.MarkerImage(
                    // URL
                    'images/end-pin.png',
                    // (image width,height)
                    new google.maps.Size(32, 32),
                    //Point for the Image
                    new google.maps.Point(0, 0)
                )
            };
            directionsDisplay.setMap(null);
            directionsDisplay.setMap(map);



            var directionsService = new google.maps.DirectionsService();
            //alert(currentLocation.latitude);" ,long:"-80.128108
            //var start = new google.maps.LatLng(36.106139,-86.799758);
            var start = new google.maps.LatLng($rootScope.placeCoordinate.coords.latitude, $rootScope.placeCoordinate.coords.longitude);
            var end = new google.maps.LatLng($rootScope.place.result.geometry.location.lat, $rootScope.place.result.geometry.location.lng);



            try {


                //alert(currentLocation.latitude);" ,long:"-80.128108
                //var start = new google.maps.LatLng(36.106139,-86.799758);


                switch (mode) {
                    case "walk":
                        {

                            $scope.walk = true;
                            $scope.cycle = false;
                            $scope.drive = false;
							$scope.transit = false;

                            var usertravelMode = "WALKING";
                            var request = {
                                origin: start,
                                destination: end,
                                travelMode: google.maps.TravelMode.WALKING,
                                unitSystem: google.maps.UnitSystem.METRIC
                            };
                            break;
                        } 
						case "TRANSIT":
                        {

                            $scope.transit = true;
                            $scope.walk = false;
                            $scope.cycle = false;
                            $scope.drive = false;


                            var usertravelMode = "TRANSIT";
                            var request = {
                                origin: start,
                                destination: end,
                                travelMode: google.maps.TravelMode.TRANSIT,
                                unitSystem: google.maps.UnitSystem.METRIC
                            };
                            break;
                        }
                    case "cycle":
                        {

                            $scope.walk = false;
                            $scope.cycle = true;
                            $scope.drive = false;
							$scope.transit = false;
                            var usertravelMode = "BICYCLING";
                            var request = {
                                origin: start,
                                destination: end,
                                travelMode: google.maps.TravelMode.BICYCLING,
                                unitSystem: google.maps.UnitSystem.METRIC
                            };

                            break;
                        }
                    case "drive":
                        {

                            $scope.walk = false;
                            $scope.cycle = false;
                            $scope.drive = true;
							$scope.transit = false;
                            var usertravelMode = "DRIVING";
                            var request = {
                                origin: start,
                                destination: end,
                                travelMode: google.maps.TravelMode.DRIVING,
                                unitSystem: google.maps.UnitSystem.METRIC
                            };

                            break;
                        }
                    default:
                        {
                            // $("#mapRadio").find("input[type='radio']").attr("checked",false).checkboxradio("refresh");
                            // $("#mapRadio").find("input[name='walking']").attr("checked",true).checkboxradio("refresh");
                            var usertravelMode = "DRIVING";
                            var request = {
                                origin: start,
                                destination: end,
                                travelMode: google.maps.TravelMode.DRIVING,
                                unitSystem: google.maps.UnitSystem.METRIC
                            };
                            break;
                        }


                }

            } catch (e) {
                alert(e)
            }

            directionsService.route(request, function(result, status) {

                if (status == google.maps.DirectionsStatus.OK) {
                    try {
                        //$(".mapRefresh").show();
                        $("#PlaceDirectionDetail").show();
                        var len = result.routes[0].legs[0].distance.text.length - 3;
                        var miles = result.routes[0].legs[0].distance.text.substring(0, len) * 0.621;
                        var d = miles.toString();
                        // alert(d.slice(0,8)+" "+result.routes[0].legs[0].distance.text.substring(0,len)+" "+len);
                        $("#PlacedirectionInfo").html("TravelMode: " + usertravelMode + "<br />Distance = <span style='white-space:nowrap; text-overflow:ellipsis; overflow:hidden;width:35px;'>" + d.slice(0, 6) + "</span> miles <br />Time = " + JSON.stringify(result.routes[0].legs[0].duration.text));
                    } catch (e) {}
                    directionsDisplay.setDirections(result);
                    var leg = result.routes[0].legs[0];
                    //PlacemakeMarker(leg.start_location, icons.start, "title");
                    var dirMarkerst = new google.maps.Marker({
                        animation: google.maps.Animation.null,
                        //draggable:true,
                        position: leg.start_location,
                        map: map,
                        title: "title"
                    });
                    //PlacemakeMarker(leg.end_location, icons.end, 'title');
                    var dirMarkerend = new google.maps.Marker({
                        animation: google.maps.Animation.null,
                        //draggable:true,
                        position: leg.end_location,
                        map: map,
                        title: "title"
                    });
                    //stopActivitySpinner();
                } else {
                    //stopActivitySpinner();
                    navigator.notification.alert("Oops! Your device don't have GPS enabled or Selected Mode on this Route not available.");

                }
            });



        }


        $scope.changeMode('default');
    } catch (e) {
        alert("Error : " + e);
    }
}
